    public void setCaja_chatP2P(String cadena){
        caja_chatP2P.append(cadena+"\n");
    }
    
    public void setServidorDirecto(ServidorDirecto servD){
        this.servD=servD;
    }
   
    public void setServidor(Servidor serv){
        this.serv=serv;
    }
   
    
    private void enviar_chatP2PActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_enviar_chatP2PActionPerformed
        if(escribir_chatP2P.getText() != null && !escribir_chatP2P.getText().isEmpty()){//Comprobamos que no sea cadena vacia 
            setCaja_chatP2P("YO -> "+escribir_chatP2P.getText());//Le a√±adimos a nuestra ventana 
            if(serv!=null){
                serv.hablar(escribir_chatP2P.getText());
            }else if(servD != null){
                servD.hablar(escribir_chatP2P.getText());
            }
        }
    }//GEN-LAST:event_enviar_chatP2PActionPerformed

    private void Salir_conversacionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Salir_conversacionActionPerformed
        if(serv!=null){
            serv.desconectar(this.getTitle());
            serv.getCliente().borrarPersonaP2P();
        }else if(servD != null){
            servD.desconectarD(this.getTitle());
            servD.getCliente().borrarPersonaP2P();
        }
    }//GEN-LAST:event_Salir_conversacionActionPerformed

